data.f_handler:
function(){
	if (store.getCount() == 0) {
		Mixky.lib.getNewTableRecordId('mkWorkReport.T_MK_APP_WORKREPORT_SCOPE.frmWorkReportScope', function(newId){
			var record = new store.recordType({ID : newId, rowstate : 'add'}, newId);
			var index = store.getCount();
			store.insert(index, record);
			grid.getSelectionModel().selectRow(index);
			var records = grid.getSelectedRecords();
			if(records.length > 0){
				var record = records[0];
				Mixky.app.common.getOrganizationWindow(null, record.get("F_SCOP_IDS"), function(display, value){
					record.set("F_SCOP_IDS", value);
					record.set("F_SCOPS", display);
				}).show();
			}
		});
	} else {
		var records = grid.getSelectedRecords();
		if(records.length > 0){
			var record = records[0];
			Mixky.app.common.getOrganizationWindow(null, record.get("F_SCOP_IDS"), function(display, value){
				record.set("F_SCOP_IDS", value);
				record.set("F_SCOPS", display);
			}).show();
		}
	}
}

function(){
	var modifieds = store.getModifiedRecords();

	var submit = function(record){
		var item = record.getChanges();
		item.ID = record.get('ID');
		item.rowstate = record.get('edit');
		DocumentAppDirect.submitRowForm('mkWorkReport.T_MK_APP_WORKREPORT_SCOPE.frmWorkReportScope', item, function(result, e){
			if(result && result.success){
				record.commit();
			}else{
				MixkyApp.showDirectActionFail("保存数据", result, e);
			}
		});
	}
	for(var i=0;i<modifieds.length;i++){
		submit(modifieds[i]);
	}
}
